from Class.login import Loginfrom Class.login import Registerfrom tkinter import *from tkinter import messagebox# Initializing Tkinterroot = Tk()root.geometry('700x500')root.resizable(0,0)root.title('Login')usernameLabel = Label(root, text = "Username")passwordLabel = Label(root, text = "Password")usernameEntry = Entry(root)passwordEntry = Entry(root, show = '*')login = Login()register = Register()def verifyLogin():    if len(usernameEntry.get())!=0 and len(passwordEntry.get())!=0:        loginClick()    else:        messagebox.showerror('Answer', "All fields must be filled")def loginClick():        check = login.checkUser(usernameEntry.get(), passwordEntry.get())    if check:        messagebox.showinfo('Correct', 'Yes, master!!')    elif check == None:        messagebox.showerror('Answer', "Sorry, user doesn't exist")    else:        messagebox.showerror('Answer', 'Sorry, incorrect password or login')    clearEntries()def clearEntries():    usernameEntry.delete(0, 'end')    passwordEntry.delete(0, 'end')loginButton = Button(root, command=verifyLogin, text="Login")registerButton = Button(root, text='Register')usernameLabel.place(x=250, y=200)passwordLabel.place(x=253, y=220)usernameEntry.place(x=315, y=200)passwordEntry.place(x=315, y=220)loginButton.place(x=305, y=250)registerButton.place(x=350, y=250)root.mainloop()